import React, { Component }  from 'react';

import Paintings from '../paintings.json';


class PaintingInfo extends Component {
    constructor(props) {
        super(props)
        this.state = {
            paintingsData: Paintings.paintings
        }
    }
    render() {
        var i
        // var paintingInfo
        // console.log(this.state.paintingsData[0].name)
        var paintingInfo
        for (i in this.state.paintingsData) {
            if (this.state.paintingsData[i].name === this.props.match.params.paintingName){
                paintingInfo = this.state.paintingsData[i]
            }
        }
        console.log(paintingInfo)
        return (
            <div className="paintingInfo">
                <div>{paintingInfo.name}</div>
                <div></div>
                <img src={'../'+paintingInfo.source} alt="Not found"></img>
            </div>
        );
    }
}

export default PaintingInfo;
